import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { doc, getDoc } from 'firebase/firestore';
import { db } from '../firebase/config';
import { getRecommendations, searchRecipes } from '../services/apiService';

const HomePage = () => {
  const { currentUser } = useAuth();
  const [userProfile, setUserProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [popularRecipes, setPopularRecipes] = useState([]);
  const [quickRecipes, setQuickRecipes] = useState([]);

  // Fetch user profile and recommendations
  useEffect(() => {
    const fetchUserData = async () => {
      setLoading(true);
      
      try {
        // Get popular recipes regardless of user login
        const popularResult = await searchRecipes('', {
          sort: 'popularity',
          number: 4
        });
        
        if (popularResult && popularResult.results) {
          setPopularRecipes(popularResult.results);
        }
        
        // Get quick recipes
        const quickResult = await searchRecipes('', {
          maxReadyTime: 30,
          sort: 'time',
          number: 4
        });
        
        if (quickResult && quickResult.results) {
          setQuickRecipes(quickResult.results);
        }
        
        // If user is logged in, get personalized recommendations
        if (currentUser) {
          const userDoc = await getDoc(doc(db, 'users', currentUser.uid));
          
          if (userDoc.exists() && userDoc.data().healthProfile) {
            const healthProfile = userDoc.data().healthProfile;
            setUserProfile(healthProfile);
            
            // Get personalized recommendations
            const recommendResult = await getRecommendations(healthProfile, { number: 8 });
            
            if (recommendResult && recommendResult.results) {
              setRecommendations(recommendResult.results);
            }
          }
        }
      } catch (err) {
        console.error('Error fetching data:', err);
        setError('Failed to load recommendations. Please try again later.');
      } finally {
        setLoading(false);
      }
    };
    
    fetchUserData();
  }, [currentUser]);

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
      {/* 새로운 헤더 섹션 */}
      <div className="rounded-2xl overflow-hidden shadow-xl mb-12 relative">
        {/* 그라데이션 배경 */}
        <div className="absolute inset-0 bg-gradient-to-br from-emerald-500 via-green-500 to-teal-500 z-0">
          {/* 물결 패턴 배경 */}
          <svg 
            className="absolute inset-0 h-full w-full opacity-10" 
            xmlns="http://www.w3.org/2000/svg" 
            viewBox="0 0 1440 320"
          >
            <path 
              fill="#ffffff" 
              fillOpacity="1" 
              d="M0,192L48,176C96,160,192,128,288,133.3C384,139,480,181,576,186.7C672,192,768,160,864,144C960,128,1056,128,1152,144C1248,160,1344,192,1392,208L1440,224L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"
            ></path>
          </svg>
          
          <svg 
            className="absolute inset-0 h-full w-full opacity-10 transform rotate-180" 
            xmlns="http://www.w3.org/2000/svg" 
            viewBox="0 0 1440 320"
          >
            <path 
              fill="#ffffff" 
              fillOpacity="1" 
              d="M0,64L48,80C96,96,192,128,288,133.3C384,139,480,117,576,133.3C672,149,768,203,864,208C960,213,1056,171,1152,149.3C1248,128,1344,128,1392,128L1440,128L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"
            ></path>
          </svg>
        </div>
        
        <div className="relative z-10 px-6 py-16 md:py-24 flex flex-col items-center text-center">
          {/* 로고 */}
          <div className="mb-8 bg-white/20 p-4 rounded-full backdrop-blur-sm">
            <img src="/images/Logo.png" alt="NutriGen Bot Logo" className="h-20 w-auto" />
          </div>
          
          {/* 헤더 텍스트 */}
          <h1 className="text-4xl md:text-5xl font-bold text-white mb-6 tracking-tight drop-shadow-lg">
            <span className="block">Healthy Recipes</span>
            <span className="block mt-2">Tailored Just for You</span>
          </h1>
          
          <p className="text-xl text-white text-opacity-90 mb-10 max-w-2xl shadow-text">
            Discover delicious meals that match your dietary needs, health goals, and personal preferences.
          </p>
          
          {/* 버튼 영역에 부드러운 배경 추가 */}
          <div className="bg-white/10 backdrop-blur-sm rounded-xl p-6 inline-block">
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link 
                to="/search" 
                className="px-8 py-3 bg-white text-green-600 rounded-full hover:bg-green-50 font-semibold text-lg shadow-lg transition-all hover:scale-105"
              >
                Find Recipes
              </Link>
              {!currentUser ? (
                <Link 
                  to="/signup" 
                  className="px-8 py-3 bg-green-700 text-white border border-green-400 rounded-full hover:bg-green-800 font-semibold text-lg shadow-lg transition-all hover:scale-105"
                >
                  Get Started
                </Link>
              ) : !userProfile ? (
                <Link 
                  to="/profile" 
                  className="px-8 py-3 bg-green-700 text-white border border-green-400 rounded-full hover:bg-green-800 font-semibold text-lg shadow-lg transition-all hover:scale-105"
                >
                  Complete Profile
                </Link>
              ) : null}
            </div>
          </div>
        </div>
      </div>
      
      {/* Personalized Recommendations Section */}
      {currentUser && userProfile && recommendations.length > 0 && (
        <div className="mb-12">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-bold text-gray-800">
              Your Personalized Recommendations
            </h2>
            <Link to="/search" className="text-green-600 hover:text-green-700 font-medium text-sm">
              View all →
            </Link>
          </div>
          
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {recommendations.slice(0, 4).map(recipe => (
              <Link
                key={recipe.id}
                to={`/recipe/${recipe.id}`}
                className="bg-white rounded-lg overflow-hidden shadow-md hover:shadow-lg transition-shadow duration-300"
              >
                <div className="relative h-48">
                  <img
                    src={recipe.image}
                    alt={recipe.title}
                    className="w-full h-full object-cover"
                  />
                  {recipe.readyInMinutes && (
                    <div className="absolute top-0 right-0 bg-green-500 text-white px-2 py-1 m-2 rounded text-xs font-medium">
                      {recipe.readyInMinutes} mins
                    </div>
                  )}
                </div>
                <div className="p-4">
                  <h3 className="text-lg font-medium text-gray-900 mb-2 overflow-hidden text-ellipsis line-clamp-2">
                    {recipe.title}
                  </h3>
                  <div className="flex flex-wrap gap-2 mb-2">
                    {recipe.vegetarian && (
                      <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-green-100 text-green-800">
                        Vegetarian
                      </span>
                    )}
                    {recipe.vegan && (
                      <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-green-100 text-green-800">
                        Vegan
                      </span>
                    )}
                    {recipe.glutenFree && (
                      <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-yellow-100 text-yellow-800">
                        Gluten Free
                      </span>
                    )}
                  </div>
                </div>
              </Link>
            ))}
          </div>
        </div>
      )}
      
      {/* Popular Recipes Section */}
      <div className="mb-12">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-gray-800">
            Popular Recipes
          </h2>
          <Link to="/search?sort=popularity" className="text-green-600 hover:text-green-700 font-medium text-sm">
            View all →
          </Link>
        </div>
        
        {loading ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="bg-white rounded-lg overflow-hidden shadow-md">
                <div className="h-48 bg-gray-200 animate-pulse"></div>
                <div className="p-4">
                  <div className="h-6 bg-gray-200 rounded animate-pulse mb-2"></div>
                  <div className="h-4 bg-gray-200 rounded animate-pulse w-3/4"></div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {popularRecipes.map(recipe => (
              <Link
                key={recipe.id}
                to={`/recipe/${recipe.id}`}
                className="bg-white rounded-lg overflow-hidden shadow-md hover:shadow-lg transition-shadow duration-300"
              >
                <div className="relative h-48">
                  <img
                    src={recipe.image}
                    alt={recipe.title}
                    className="w-full h-full object-cover"
                  />
                  {recipe.readyInMinutes && (
                    <div className="absolute top-0 right-0 bg-green-500 text-white px-2 py-1 m-2 rounded text-xs font-medium">
                      {recipe.readyInMinutes} mins
                    </div>
                  )}
                </div>
                <div className="p-4">
                  <h3 className="text-lg font-medium text-gray-900 mb-2 overflow-hidden text-ellipsis line-clamp-2">
                    {recipe.title}
                  </h3>
                  <div className="flex flex-wrap gap-2">
                    {recipe.vegetarian && (
                      <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-green-100 text-green-800">
                        Vegetarian
                      </span>
                    )}
                    {recipe.vegan && (
                      <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-green-100 text-green-800">
                        Vegan
                      </span>
                    )}
                    {recipe.glutenFree && (
                      <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-yellow-100 text-yellow-800">
                        Gluten Free
                      </span>
                    )}
                  </div>
                </div>
              </Link>
            ))}
          </div>
        )}
      </div>
      
      {/* Quick Meals Section */}
      <div className="mb-12">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-gray-800">
            Quick Meals (Under 30 Minutes)
          </h2>
          <Link to="/search?maxReadyTime=30" className="text-green-600 hover:text-green-700 font-medium text-sm">
            View all →
          </Link>
        </div>
        
        {loading ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="bg-white rounded-lg overflow-hidden shadow-md">
                <div className="h-48 bg-gray-200 animate-pulse"></div>
                <div className="p-4">
                  <div className="h-6 bg-gray-200 rounded animate-pulse mb-2"></div>
                  <div className="h-4 bg-gray-200 rounded animate-pulse w-3/4"></div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {quickRecipes.map(recipe => (
              <Link
                key={recipe.id}
                to={`/recipe/${recipe.id}`}
                className="bg-white rounded-lg overflow-hidden shadow-md hover:shadow-lg transition-shadow duration-300"
              >
                <div className="relative h-48">
                  <img
                    src={recipe.image}
                    alt={recipe.title}
                    className="w-full h-full object-cover"
                  />
                  <div className="absolute top-0 right-0 bg-green-500 text-white px-2 py-1 m-2 rounded text-xs font-medium">
                    {recipe.readyInMinutes} mins
                  </div>
                </div>
                <div className="p-4">
                  <h3 className="text-lg font-medium text-gray-900 mb-2 overflow-hidden text-ellipsis line-clamp-2">
                    {recipe.title}
                  </h3>
                  <div className="flex flex-wrap gap-2">
                    {recipe.vegetarian && (
                      <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-green-100 text-green-800">
                        Vegetarian
                      </span>
                    )}
                    {recipe.vegan && (
                      <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-green-100 text-green-800">
                        Vegan
                      </span>
                    )}
                    {recipe.glutenFree && (
                      <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-yellow-100 text-yellow-800">
                        Gluten Free
                      </span>
                    )}
                  </div>
                </div>
              </Link>
            ))}
          </div>
        )}
      </div>
      
      {/* Call to Action */}
      {!currentUser && (
        <div className="bg-white rounded-xl p-8 text-center shadow-md">
          <h2 className="text-2xl font-semibold text-gray-900 mb-4">
            Get Personalized Recipe Recommendations
          </h2>
          <p className="text-gray-600 mb-6 max-w-3xl mx-auto">
            Create an account and complete your health profile to receive personalized recipe recommendations based on your dietary preferences, health goals, and food allergies.
          </p>
          <Link
            to="/signup"
            className="inline-flex items-center px-6 py-3 border border-transparent text-base font-medium rounded-md shadow-sm text-white bg-green-600 hover:bg-green-700"
          >
            Sign Up Now
          </Link>
        </div>
      )}
    </div>
  );
};

export default HomePage;
