import React, { useState } from 'react';
import { Tabs, Tab } from '../components/common/Tabs';
import HealthProfileForm from '../components/profile/HealthProfileForm';
import UserStats from '../components/profile/UserStats';
import FavoriteRecipes from '../components/profile/FavoriteRecipes';
import { useAuth } from '../hooks/useAuth';

const ProfilePage = () => {
  const [activeTab, setActiveTab] = useState('nutrition');
  const { user } = useAuth();

  const handleTabChange = (tabId) => {
    setActiveTab(tabId);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-green-500 text-white p-6 rounded-lg mb-6 flex items-center justify-between">
        <div className="flex items-center">
          <div className="mr-4">
            <img src="/images/Logo.png" alt="NutriGen Bot Logo" className="w-12 h-12" />
          </div>
          <div>
            <h1 className="text-2xl font-bold">My Health Profile</h1>
            <p>Personalize your nutrition journey</p>
          </div>
        </div>
        {/* Sign out button removed from here */}
      </div>

      <Tabs activeTab={activeTab} onChange={handleTabChange}>
        <Tab id="health" label="Health Profile">
          <HealthProfileForm />
        </Tab>
        <Tab id="nutrition" label="Nutrition Dashboard">
          <UserStats user={user} />
        </Tab>
        <Tab id="saved" label="Saved Recipes">
          <FavoriteRecipes />
        </Tab>
      </Tabs>
    </div>
  );
};

export default ProfilePage;
