import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { doc, updateDoc, getDoc } from 'firebase/firestore';
import { db } from '../firebase/config';

const ProfilePage = () => {
  const { currentUser, logout } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  
  // 사용자 건강 정보 상태
  const [healthProfile, setHealthProfile] = useState({
    height: '',
    weight: '',
    age: '',
    gender: '',
    activityLevel: '',
    goal: '',
    allergies: [],
    dietaryRestrictions: []
  });
  
  // 알러지 및 식이 제한 옵션
  const allergyOptions = [
    'Dairy', 'Egg', 'Gluten', 'Grain', 'Peanut', 'Seafood', 
    'Sesame', 'Shellfish', 'Soy', 'Sulfite', 'Tree Nut', 'Wheat'
  ];
  
  const dietaryRestrictionOptions = [
    'Gluten Free', 'Ketogenic', 'Vegetarian', 'Lacto-Vegetarian', 
    'Ovo-Vegetarian', 'Vegan', 'Pescetarian', 'Paleo', 'Primal', 'Low FODMAP'
  ];
  
  // 활동 수준 옵션
  const activityLevels = [
    { value: 'sedentary', label: 'Sedentary (little or no exercise)' },
    { value: 'light', label: 'Lightly active (light exercise/sports 1-3 days/week)' },
    { value: 'moderate', label: 'Moderately active (moderate exercise/sports 3-5 days/week)' },
    { value: 'active', label: 'Very active (hard exercise/sports 6-7 days a week)' },
    { value: 'veryActive', label: 'Super active (very hard exercise & physical job or 2x training)' }
  ];
  
  // 목표 옵션
  const goalOptions = [
    { value: 'weightLoss', label: 'Weight Loss' },
    { value: 'weightMaintenance', label: 'Weight Maintenance' },
    { value: 'weightGain', label: 'Weight Gain' }
  ];

  // 사용자 데이터 로드
  useEffect(() => {
    const fetchUserData = async () => {
      if (!currentUser) return;
      
      try {
        setLoading(true);
        const userDoc = await getDoc(doc(db, 'users', currentUser.uid));
        
        if (userDoc.exists() && userDoc.data().healthProfile) {
          setHealthProfile(userDoc.data().healthProfile);
        }
      } catch (err) {
        console.error('Error fetching user data:', err);
        setError('Failed to load your health profile. Please try again.');
      } finally {
        setLoading(false);
      }
    };
    
    fetchUserData();
  }, [currentUser]);

  // 입력 폼 변경 핸들러
  const handleChange = (e) => {
    const { name, value } = e.target;
    setHealthProfile(prev => ({
      ...prev,
      [name]: value
    }));
  };
  
  // 알러지 체크박스 핸들러
  const handleAllergyChange = (allergy) => {
    setHealthProfile(prev => {
      const allergies = [...prev.allergies];
      
      if (allergies.includes(allergy)) {
        return {
          ...prev,
          allergies: allergies.filter(item => item !== allergy)
        };
      } else {
        return {
          ...prev,
          allergies: [...allergies, allergy]
        };
      }
    });
  };
  
  // 식이 제한 체크박스 핸들러
  const handleDietaryRestrictionChange = (restriction) => {
    setHealthProfile(prev => {
      const restrictions = [...prev.dietaryRestrictions];
      
      if (restrictions.includes(restriction)) {
        return {
          ...prev,
          dietaryRestrictions: restrictions.filter(item => item !== restriction)
        };
      } else {
        return {
          ...prev,
          dietaryRestrictions: [...restrictions, restriction]
        };
      }
    });
  };

  // 프로필 저장 핸들러
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!currentUser) {
      setError('You must be logged in to update your profile.');
      return;
    }
    
    try {
      setLoading(true);
      setError('');
      setMessage('');
      
      // BMR 계산 (Harris-Benedict 방정식)
      let bmr = 0;
      if (healthProfile.gender && healthProfile.weight && healthProfile.height && healthProfile.age) {
        const weight = parseFloat(healthProfile.weight);
        const height = parseFloat(healthProfile.height);
        const age = parseInt(healthProfile.age);
        
        if (healthProfile.gender === 'male') {
          bmr = 88.362 + (13.397 * weight) + (4.799 * height) - (5.677 * age);
        } else {
          bmr = 447.593 + (9.247 * weight) + (3.098 * height) - (4.330 * age);
        }
        
        // 활동 수준에 따른 조정
        const activityMultipliers = {
          sedentary: 1.2,
          light: 1.375,
          moderate: 1.55,
          active: 1.725,
          veryActive: 1.9
        };
        
        const tdee = bmr * (activityMultipliers[healthProfile.activityLevel] || 1.2);
        
        // 목표에 따른 칼로리 조정
        let targetCalories = tdee;
        if (healthProfile.goal === 'weightLoss') {
          targetCalories = tdee - 500; // 체중 감량은 하루 500 칼로리 적게
        } else if (healthProfile.goal === 'weightGain') {
          targetCalories = tdee + 500; // 체중 증가는 하루 500 칼로리 더
        }
        
        // 계산된 값 추가
        healthProfile.bmr = Math.round(bmr);
        healthProfile.tdee = Math.round(tdee);
        healthProfile.targetCalories = Math.round(targetCalories);
      }
      
      // Firestore 문서 업데이트
      await updateDoc(doc(db, 'users', currentUser.uid), {
        healthProfile,
        updatedAt: new Date()
      });
      
      setMessage('Your health profile has been updated successfully!');
    } catch (err) {
      console.error('Error updating profile:', err);
      setError('Failed to update your profile. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // 로그아웃 핸들러
  const handleLogout = async () => {
    try {
      await logout();
      navigate('/login');
    } catch (err) {
      console.error('Failed to log out', err);
      setError('Failed to log out.');
    }
  };

  if (!currentUser) {
    return (
      <div className="max-w-4xl mx-auto p-6">
        <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4">
          <div className="flex">
            <div className="flex-shrink-0">
              <svg className="h-5 w-5 text-yellow-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
              </svg>
            </div>
            <div className="ml-3">
              <p className="text-sm text-yellow-700">
                You need to be logged in to view this page.{' '}
                <button
                  onClick={() => navigate('/login')}
                  className="font-medium underline text-yellow-700 hover:text-yellow-600"
                >
                  Sign in
                </button>
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto p-6">
      <div className="bg-white shadow-md rounded-lg overflow-hidden">
        <div className="px-4 py-5 sm:px-6 flex justify-between items-center border-b border-gray-200">
          <div>
            <h1 className="text-lg leading-6 font-medium text-gray-900">Profile</h1>
            <p className="mt-1 max-w-2xl text-sm text-gray-500">
              Update your health information to get personalized recipe recommendations.
            </p>
          </div>
          <button
            onClick={handleLogout}
            className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2"
          >
            Sign out
          </button>
        </div>
        
        {error && (
          <div className="bg-red-50 border-l-4 border-red-500 p-4 m-4">
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-red-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-9v4a1 1 0 11-2 0v-4a1 1 0 112 0zm0-4a1 1 0 11-2 0 1 1 0 012 0z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <p className="text-sm text-red-700">{error}</p>
              </div>
            </div>
          </div>
        )}
        
        {message && (
          <div className="bg-green-50 border-l-4 border-green-500 p-4 m-4">
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-green-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <p className="text-sm text-green-700">{message}</p>
              </div>
            </div>
          </div>
        )}
        
        <div className="px-4 py-5 sm:p-6">
          <div className="mb-6">
            <div className="bg-blue-50 border-l-4 border-blue-500 p-4">
              <div className="flex">
                <div className="flex-shrink-0">
                  <svg className="h-5 w-5 text-blue-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2h-1V9a1 1 0 00-1-1z" clipRule="evenodd" />
                  </svg>
                </div>
                <div className="ml-3">
                  <p className="text-sm text-blue-700">
                    Your personal information helps us provide better recipe recommendations and calculate your daily caloric needs.
                  </p>
                </div>
              </div>
            </div>
          </div>
          
          <form onSubmit={handleSubmit}>
            {/* Basic Information */}
            <div className="mb-6">
              <h2 className="text-lg font-medium text-gray-900 mb-4">Basic Information</h2>
              <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
                <div>
                  <label htmlFor="height" className="block text-sm font-medium text-gray-700">
                    Height (cm)
                  </label>
                  <input
                    type="number"
                    name="height"
                    id="height"
                    value={healthProfile.height}
                    onChange={handleChange}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-green-500 focus:border-green-500 sm:text-sm"
                  />
                </div>
                
                <div>
                  <label htmlFor="weight" className="block text-sm font-medium text-gray-700">
                    Weight (kg)
                  </label>
                  <input
                    type="number"
                    name="weight"
                    id="weight"
                    value={healthProfile.weight}
                    onChange={handleChange}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-green-500 focus:border-green-500 sm:text-sm"
                  />
                </div>
                
                <div>
                  <label htmlFor="age" className="block text-sm font-medium text-gray-700">
                    Age
                  </label>
                  <input
                    type="number"
                    name="age"
                    id="age"
                    value={healthProfile.age}
                    onChange={handleChange}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-green-500 focus:border-green-500 sm:text-sm"
                  />
                </div>
                
                <div>
                  <label htmlFor="gender" className="block text-sm font-medium text-gray-700">
                    Gender
                  </label>
                  <select
                    name="gender"
                    id="gender"
                    value={healthProfile.gender}
                    onChange={handleChange}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-green-500 focus:border-green-500 sm:text-sm"
                  >
                    <option value="">Select gender</option>
                    <option value="male">Male</option>
                    <option value="female">Female</option>
                  </select>
                </div>
              </div>
            </div>
            
            {/* Activity & Goals */}
            <div className="mb-6">
              <h2 className="text-lg font-medium text-gray-900 mb-4">Activity & Goals</h2>
              <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
                <div>
                  <label htmlFor="activityLevel" className="block text-sm font-medium text-gray-700">
                    Activity Level
                  </label>
                  <select
                    name="activityLevel"
                    id="activityLevel"
                    value={healthProfile.activityLevel}
                    onChange={handleChange}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-green-500 focus:border-green-500 sm:text-sm"
                  >
                    <option value="">Select activity level</option>
                    {activityLevels.map(level => (
                      <option key={level.value} value={level.value}>
                        {level.label}
                      </option>
                    ))}
                  </select>
                </div>
                
                <div>
                  <label htmlFor="goal" className="block text-sm font-medium text-gray-700">
                    Goal
                  </label>
                  <select
                    name="goal"
                    id="goal"
                    value={healthProfile.goal}
                    onChange={handleChange}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-green-500 focus:border-green-500 sm:text-sm"
                  >
                    <option value="">Select goal</option>
                    {goalOptions.map(goal => (
                      <option key={goal.value} value={goal.value}>
                        {goal.label}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            </div>
            
            {/* Allergies */}
            <div className="mb-6">
              <h2 className="text-lg font-medium text-gray-900 mb-4">Allergies</h2>
              <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
                {allergyOptions.map(allergy => (
                  <div key={allergy} className="flex items-center">
                    <input
                      id={`allergy-${allergy}`}
                      name={`allergy-${allergy}`}
                      type="checkbox"
                      checked={healthProfile.allergies.includes(allergy)}
                      onChange={() => handleAllergyChange(allergy)}
                      className="h-4 w-4 text-green-600 focus:ring-green-500 border-gray-300 rounded"
                    />
                    <label htmlFor={`allergy-${allergy}`} className="ml-2 block text-sm text-gray-700">
                      {allergy}
                    </label>
                  </div>
                ))}
              </div>
            </div>
            
            {/* Dietary Restrictions */}
            <div className="mb-6">
              <h2 className="text-lg font-medium text-gray-900 mb-4">Dietary Restrictions</h2>
              <div className="grid grid-cols-2 sm:grid-cols-3 gap-4">
                {dietaryRestrictionOptions.map(restriction => (
                  <div key={restriction} className="flex items-center">
                    <input
                      id={`restriction-${restriction}`}
                      name={`restriction-${restriction}`}
                      type="checkbox"
                      checked={healthProfile.dietaryRestrictions.includes(restriction)}
                      onChange={() => handleDietaryRestrictionChange(restriction)}
                      className="h-4 w-4 text-green-600 focus:ring-green-500 border-gray-300 rounded"
                    />
                    <label htmlFor={`restriction-${restriction}`} className="ml-2 block text-sm text-gray-700">
                      {restriction}
                    </label>
                  </div>
                ))}
              </div>
            </div>
            
            <div className="flex justify-end">
              <button
                type="submit"
                disabled={loading}
                className="ml-3 inline-flex justify-center py-2 px-4 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 disabled:opacity-50"
              >
                {loading ? 'Saving...' : 'Save Profile'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

