// src/components/search/TrendingRecipes.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';

const TrendingRecipes = ({ setIsOpen }) => {
  const navigate = useNavigate();

  // Mock data for trending recipes
  // In a real application, this would come from an API or Firebase
  const trendingRecipes = [
    {
      id: 716429,
      title: "Pasta with Garlic",
      image: "https://spoonacular.com/recipeImages/716429-312x231.jpg",
      views: 243
    },
    {
      id: 715538,
      title: "Maple Dijon Chicken",
      image: "https://spoonacular.com/recipeImages/715538-312x231.jpg",
      views: 189
    },
    {
      id: 715594,
      title: "Homemade Garlic Bread",
      image: "https://spoonacular.com/recipeImages/715594-312x231.jpg",
      views: 156
    },
    {
      id: 642129,
      title: "Easy Vegetable Soup",
      image: "https://spoonacular.com/recipeImages/642129-312x231.jpg",
      views: 143
    },
    {
      id: 663126,
      title: "Thai Basil Chicken",
      image: "https://spoonacular.com/recipeImages/663126-312x231.jpg",
      views: 129
    }
  ];

  const handleRecipeClick = (recipeId) => {
    navigate(`/recipe/${recipeId}`);
    if (setIsOpen) setIsOpen(false);
  };

  return (
    <div className="space-y-4">
      {trendingRecipes.map((recipe, index) => (
        <div 
          key={recipe.id}
          onClick={() => handleRecipeClick(recipe.id)}
          className="flex items-center space-x-3 p-2 hover:bg-gray-50 rounded cursor-pointer transition duration-150"
        >
          <div className="text-emerald-700 font-semibold">{index + 1}</div>
          <div className="h-12 w-12 flex-shrink-0">
            <img 
              src={recipe.image} 
              alt={recipe.title} 
              className="h-full w-full object-cover rounded"
            />
          </div>
          <div className="flex-1">
            <h4 className="text-sm font-medium text-gray-800 line-clamp-2">{recipe.title}</h4>
            <p className="text-xs text-gray-500">{recipe.views} views today</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default TrendingRecipes;
