import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const SearchSidebar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [keyword, setKeyword] = useState('');
  const [savedKeywords, setSavedKeywords] = useState([]);
  const navigate = useNavigate();

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const handleKeywordChange = (e) => {
    setKeyword(e.target.value);
  };

  const addKeyword = () => {
    if (keyword && !savedKeywords.includes(keyword)) {
      setSavedKeywords([...savedKeywords, keyword]);
      setKeyword('');
    }
  };

  const removeKeyword = (keywordToRemove) => {
    setSavedKeywords(savedKeywords.filter(k => k !== keywordToRemove));
  };

  const searchRecipes = () => {
    if (savedKeywords.length > 0) {
      const queryParams = new URLSearchParams();
      savedKeywords.forEach(k => queryParams.append('keyword', k));
      navigate(`/recipes?${queryParams.toString()}`);
      setIsOpen(false);
    }
  };

  return (
    <div className="fixed right-0 top-1/3 z-50">
      {/* 눈에 잘 띄는 보라색 검색 버튼 */}
      <button
        onClick={toggleSidebar}
        className="bg-purple-700 hover:bg-purple-800 text-white p-3 rounded-l-lg shadow-xl focus:outline-none transition-all duration-300 transform hover:scale-105"
        aria-label="Toggle search sidebar"
        style={{ width: '50px', height: '50px', display: 'flex', justifyContent: 'center', alignItems: 'center' }}
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
        </svg>
      </button>

      {/* 사이드바 */}
      <div 
        className={`fixed top-0 right-0 h-full w-80 bg-white shadow-2xl transform transition-transform duration-300 ease-in-out z-50 ${
          isOpen ? 'translate-x-0' : 'translate-x-full'
        }`}
      >
        <div className="p-6 h-full flex flex-col">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-semibold">검색하기</h2>
            <button 
              onClick={toggleSidebar}
              className="text-gray-500 hover:text-gray-700 focus:outline-none"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>
          
          <div className="mb-4">
            <label htmlFor="keyword" className="block text-sm font-medium text-gray-700 mb-1">
              키워드 추가
            </label>
            <div className="flex">
              <input
                type="text"
                id="keyword"
                value={keyword}
                onChange={handleKeywordChange}
                onKeyPress={(e) => e.key === 'Enter' && addKeyword()}
                className="flex-1 p-2 border border-gray-300 rounded-l-md focus:ring-purple-500 focus:border-purple-500"
                placeholder="키워드 입력"
              />
              <button
                onClick={addKeyword}
                className="bg-purple-600 text-white px-4 rounded-r-md hover:bg-purple-700 focus:outline-none"
              >
                추가
              </button>
            </div>
          </div>
          
          <div className="mb-4 flex-1 overflow-y-auto">
            <p className="text-sm font-medium text-gray-700 mb-2">추가된 키워드:</p>
            {savedKeywords.length > 0 ? (
              <div className="flex flex-wrap gap-2">
                {savedKeywords.map((k, index) => (
                  <div key={index} className="flex items-center bg-purple-100 text-purple-800 px-2 py-1 rounded-md">
                    <span>{k}</span>
                    <button 
                      onClick={() => removeKeyword(k)}
                      className="ml-1 text-purple-600 hover:text-purple-800 focus:outline-none"
                    >
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                      </svg>
                    </button>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-gray-500 text-sm">아직 추가된 키워드가 없습니다.</p>
            )}
          </div>
          
          <button
            onClick={searchRecipes}
            disabled={savedKeywords.length === 0}
            className={`w-full py-2 px-4 rounded-md ${
              savedKeywords.length > 0
                ? 'bg-purple-600 text-white hover:bg-purple-700'
                : 'bg-gray-300 text-gray-500 cursor-not-allowed'
            }`}
          >
            레시피 검색
          </button>
        </div>
      </div>
    </div>
  );
};

export default SearchSidebar;
