import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { doc, getDoc } from 'firebase/firestore';
import { db } from '../firebase/config';
import { getRecommendations, searchRecipes } from '../services/apiService';

const HomePage = () => {
  const { currentUser } = useAuth();
  const [userProfile, setUserProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [popularRecipes, setPopularRecipes] = useState([]);
  const [quickRecipes, setQuickRecipes] = useState([]);
  const [categories, setCategories] = useState([
    { name: "Breakfast", image: "https://spoonacular.com/recipeImages/715497-312x231.jpg", link: "/search?type=breakfast" },
    { name: "Lunch", image: "https://spoonacular.com/recipeImages/715415-312x231.jpg", link: "/search?type=lunch" },
    { name: "Dinner", image: "https://spoonacular.com/recipeImages/716429-312x231.jpg", link: "/search?type=dinner" },
    { name: "Snacks", image: "https://spoonacular.com/recipeImages/715543-312x231.jpg", link: "/search?type=snack" }
  ]);

  // Fetch user profile and recommendations
  useEffect(() => {
    const fetchUserData = async () => {
      setLoading(true);
      
      try {
        // Get popular recipes regardless of user login
        const popularResult = await searchRecipes('', {
          sort: 'popularity',
          number: 4
        });
        
        if (popularResult && popularResult.results) {
          setPopularRecipes(popularResult.results);
        }
        
        // Get quick recipes
        const quickResult = await searchRecipes('', {
          maxReadyTime: 30,
          sort: 'time',
          number: 4
        });
        
        if (quickResult && quickResult.results) {
          setQuickRecipes(quickResult.results);
        }
        
        // If user is logged in, get personalized recommendations
        if (currentUser) {
          const userDoc = await getDoc(doc(db, 'users', currentUser.uid));
          
          if (userDoc.exists() && userDoc.data().healthProfile) {
            const healthProfile = userDoc.data().healthProfile;
            setUserProfile(healthProfile);
            
            // Get personalized recommendations
            const recommendResult = await getRecommendations(healthProfile, { number: 8 });
            
            if (recommendResult && recommendResult.results) {
              setRecommendations(recommendResult.results);
            }
          }
        }
      } catch (err) {
        console.error('Error fetching data:', err);
        setError('Failed to load recommendations. Please try again later.');
      } finally {
        setLoading(false);
      }
    };
    
    fetchUserData();
  }, [currentUser]);

  // Recipe Card Component for reuse
  const RecipeCard = ({ recipe }) => (
    <Link
      to={`/recipe/${recipe.id}`}
      className="group bg-white rounded-xl overflow-hidden shadow-md hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1"
    >
      <div className="relative h-48 overflow-hidden">
        <img
          src={recipe.image}
          alt={recipe.title}
          className="w-full h-full object-cover transform group-hover:scale-105 transition-transform duration-300"
        />
        {recipe.readyInMinutes && (
          <div className="absolute top-0 right-0 bg-green-600 text-white px-3 py-1 m-2 rounded-lg text-xs font-medium shadow-md">
            {recipe.readyInMinutes} mins
          </div>
        )}
      </div>
      <div className="p-5">
        <h3 className="text-lg font-semibold text-gray-800 mb-3 line-clamp-2 group-hover:text-green-600 transition-colors">
          {recipe.title}
        </h3>
        <div className="flex flex-wrap gap-2 mb-2">
          {recipe.vegetarian && (
            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800 border border-green-200">
              Vegetarian
            </span>
          )}
          {recipe.vegan && (
            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800 border border-green-200">
              Vegan
            </span>
          )}
          {recipe.glutenFree && (
            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800 border border-yellow-200">
              Gluten Free
            </span>
          )}
          {recipe.dairyFree && (
            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800 border border-blue-200">
              Dairy Free
            </span>
          )}
        </div>
        <div className="pt-2 mt-2 border-t border-gray-100">
          <div className="flex items-center text-sm text-gray-500">
            <svg className="w-4 h-4 text-yellow-500 mr-1" fill="currentColor" viewBox="0 0 20 20">
              <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
            </svg>
            <span>{recipe.spoonacularScore ? Math.round(recipe.spoonacularScore / 20) : 4.5}/5</span>
          </div>
        </div>
      </div>
    </Link>
  );

  // Section Title Component
  const SectionTitle = ({ title, viewAllLink, viewAllText = "View all" }) => (
    <div className="flex justify-between items-center mb-8">
      <h2 className="text-2xl md:text-3xl font-bold text-gray-800 border-l-4 border-green-500 pl-4">
        {title}
      </h2>
      {viewAllLink && (
        <Link to={viewAllLink} className="text-green-600 hover:text-green-700 font-medium flex items-center group">
          <span className="mr-1">{viewAllText}</span>
          <svg 
            xmlns="http://www.w3.org/2000/svg" 
            className="h-5 w-5 transform group-hover:translate-x-1 transition-transform" 
            viewBox="0 0 20 20" 
            fill="currentColor"
          >
            <path fillRule="evenodd" d="M12.293 5.293a1 1 0 011.414 0l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414-1.414L14.586 11H3a1 1 0 110-2h11.586l-2.293-2.293a1 1 0 010-1.414z" clipRule="evenodd" />
          </svg>
        </Link>
      )}
    </div>
  );

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 font-sans">
      {/* Hero Section */}
      <div className="bg-gradient-to-br from-green-600 via-green-500 to-emerald-400 rounded-2xl shadow-2xl overflow-hidden mb-16">
        <div className="relative px-6 py-20 md:py-24 md:px-12 text-center">
          {/* Background pattern */}
          <div className="absolute inset-0 opacity-10">
            <svg xmlns="http://www.w3.org/2000/svg" width="100%" height="100%">
              <defs>
                <pattern id="pattern" width="80" height="80" patternUnits="userSpaceOnUse">
                  <path d="M0,40 L40,0 L80,40 L40,80 Z" fill="none" stroke="currentColor" strokeWidth="2"></path>
                </pattern>
              </defs>
              <rect width="100%" height="100%" fill="url(#pattern)" />
            </svg>
          </div>
          
          {/* Logo */}
          <div className="flex justify-center mb-6">
            <img src="/images/Logo.png" alt="NutriGen Bot Logo" className="h-24 w-auto" />
          </div>
          
          <h1 className="text-4xl md:text-5xl font-extrabold text-white mb-6">
            <span className="block">Healthy Recipes</span>
            <span className="block mt-2">Tailored Just for You</span>
          </h1>
          <p className="text-xl md:text-2xl text-green-50 mb-10 max-w-3xl mx-auto leading-relaxed">
            Discover delicious meals that match your dietary needs, health goals, and personal preferences.
          </p>
          <div className="flex flex-col sm:flex-row gap-5 justify-center">
            <Link 
              to="/search" 
              className="px-8 py-4 bg-white text-green-600 rounded-full hover:bg-green-50 shadow-lg font-semibold text-lg transform transition hover:-translate-y-1 hover:shadow-xl"
            >
              Find Recipes
            </Link>
            {!currentUser ? (
              <Link 
                to="/signup" 
                className="px-8 py-4 bg-green-700 text-white border border-green-400 rounded-full hover:bg-green-800 shadow-lg font-semibold text-lg transform transition hover:-translate-y-1 hover:shadow-xl"
              >
                Get Started
              </Link>
            ) : !userProfile ? (
              <Link 
                to="/profile" 
                className="px-8 py-4 bg-green-700 text-white border border-green-400 rounded-full hover:bg-green-800 shadow-lg font-semibold text-lg transform transition hover:-translate-y-1 hover:shadow-xl"
              >
                Complete Profile
              </Link>
            ) : null}
          </div>
        </div>
      </div>
      
      {/* Categories Section */}
      <div className="mb-16">
        <SectionTitle title="Browse by Categories" />
        
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 sm:gap-6">
          {categories.map((category, index) => (
            <Link
              key={index}
              to={category.link}
              className="relative overflow-hidden rounded-xl shadow-lg group"
            >
              <div className="absolute inset-0 bg-gradient-to-b from-transparent via-green-900/20 to-green-900/70 z-10"></div>
              <img 
                src={category.image} 
                alt={category.name} 
                className="w-full h-36 md:h-44 object-cover transform group-hover:scale-110 transition-transform duration-300" 
              />
              <div className="absolute bottom-0 left-0 right-0 p-4 text-center z-20">
                <h3 className="text-xl font-bold text-white">
                  {category.name}
                </h3>
              </div>
            </Link>
          ))}
        </div>
      </div>
      
      {/* Personalized Recommendations Section */}
      {currentUser && userProfile && recommendations.length > 0 && (
        <div className="mb-16">
          <SectionTitle 
            title="Your Personalized Recommendations" 
            viewAllLink="/search" 
          />
          
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {recommendations.slice(0, 4).map(recipe => (
              <RecipeCard key={recipe.id} recipe={recipe} />
            ))}
          </div>
        </div>
      )}
      
      {/* Popular Recipes Section */}
      <div className="mb-16">
        <SectionTitle 
          title="Popular Recipes" 
          viewAllLink="/search?sort=popularity" 
        />
        
        {loading ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="bg-white rounded-xl overflow-hidden shadow-md">
                <div className="h-48 bg-gray-200 animate-pulse"></div>
                <div className="p-5">
                  <div className="h-6 bg-gray-200 rounded animate-pulse mb-4"></div>
                  <div className="h-4 bg-gray-200 rounded animate-pulse w-3/4 mb-2"></div>
                  <div className="h-4 bg-gray-200 rounded animate-pulse w-1/2"></div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {popularRecipes.map(recipe => (
              <RecipeCard key={recipe.id} recipe={recipe} />
            ))}
          </div>
        )}
      </div>
      
      {/* Features Section */}
      <div className="mb-16 bg-green-50 rounded-2xl p-8 md:p-10">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-10">
          Why Choose NutriGen Bot?
        </h2>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          <div className="bg-white p-6 rounded-xl shadow-md text-center">
            <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <svg className="w-8 h-8 text-green-600" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
              </svg>
            </div>
            <h3 className="text-xl font-semibold text-gray-800 mb-2">Personalized Nutrition</h3>
            <p className="text-gray-600">
              Get recipe recommendations based on your health profile, dietary needs, and personal goals.
            </p>
          </div>
          
          <div className="bg-white p-6 rounded-xl shadow-md text-center">
            <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <svg className="w-8 h-8 text-blue-600" fill="currentColor" viewBox="0 0 20 20">
                <path d="M9 2a1 1 0 000 2h2a1 1 0 100-2H9z" />
                <path fillRule="evenodd" d="M4 5a2 2 0 012-2 3 3 0 003 3h2a3 3 0 003-3 2 2 0 012 2v11a2 2 0 01-2 2H6a2 2 0 01-2-2V5zm3 4a1 1 0 000 2h.01a1 1 0 100-2H7zm3 0a1 1 0 000 2h3a1 1 0 100-2h-3zm-3 4a1 1 0 100 2h.01a1 1 0 100-2H7zm3 0a1 1 0 100 2h3a1 1 0 100-2h-3z" clipRule="evenodd" />
              </svg>
            </div>
            <h3 className="text-xl font-semibold text-gray-800 mb-2">Detailed Nutrition Facts</h3>
            <p className="text-gray-600">
              View complete nutritional information for every recipe to make informed dietary choices.
            </p>
          </div>
          
          <div className="bg-white p-6 rounded-xl shadow-md text-center">
            <div className="w-16 h-16 bg-yellow-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <svg className="w-8 h-8 text-yellow-600" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M5 2a1 1 0 011 1v1h1a1 1 0 010 2H6v1a1 1 0 01-2 0V6H3a1 1 0 010-2h1V3a1 1 0 011-1zm0 10a1 1 0 011 1v1h1a1 1 0 110 2H6v1a1 1 0 11-2 0v-1H3a1 1 0 110-2h1v-1a1 1 0 011-1zM12 2a1 1 0 01.967.744L14.146 7.2 17.5 9.134a1 1 0 010 1.732l-3.354 1.935-1.18 4.455a1 1 0 01-1.933 0L9.854 12.8 6.5 10.866a1 1 0 010-1.732l3.354-1.935 1.18-4.455A1 1 0 0112 2z" clipRule="evenodd" />
              </svg>
            </div>
            <h3 className="text-xl font-semibold text-gray-800 mb-2">Dietary Restrictions</h3>
            <p className="text-gray-600">
              Filter out recipes that don't match your allergies or dietary preferences.
            </p>
          </div>
        </div>
      </div>
      
      {/* Quick Meals Section */}
      <div className="mb-16">
        <SectionTitle 
          title="Quick & Easy Meals" 
          viewAllLink="/search?maxReadyTime=30" 
          viewAllText="More quick recipes"
        />
        
        {loading ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="bg-white rounded-xl overflow-hidden shadow-md">
                <div className="h-48 bg-gray-200 animate-pulse"></div>
                <div className="p-5">
                  <div className="h-6 bg-gray-200 rounded animate-pulse mb-4"></div>
                  <div className="h-4 bg-gray-200 rounded animate-pulse w-3/4 mb-2"></div>
                  <div className="h-4 bg-gray-200 rounded animate-pulse w-1/2"></div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {quickRecipes.map(recipe => (
              <RecipeCard key={recipe.id} recipe={recipe} />
            ))}
          </div>
        )}
      </div>
      
      {/* Call to Action */}
      {!currentUser && (
        <div className="bg-gradient-to-r from-green-50 to-emerald-50 rounded-2xl p-10 text-center shadow-lg border border-green-100">
          <div className="max-w-3xl mx-auto">
            <h2 className="text-3xl font-bold text-gray-800 mb-6">
              Start Your Healthy Eating Journey Today
            </h2>
            <p className="text-lg text-gray-600 mb-8 leading-relaxed">
              Create your account and complete your health profile to receive personalized recipe recommendations tailored to your unique nutritional needs and preferences.
            </p>
            <Link
              to="/signup"
              className="inline-flex items-center px-8 py-4 border border-transparent text-lg font-semibold rounded-full shadow-lg text-white bg-gradient-to-r from-green-600 to-emerald-500 hover:from-green-700 hover:to-emerald-600 transform transition hover:-translate-y-1 hover:shadow-xl"
            >
              Create Your Free Account
            </Link>
            <p className="mt-4 text-sm text-gray-500">
              Already have an account? <Link to="/login" className="text-green-600 hover:text-green-700 font-medium">Sign in</Link>
            </p>
          </div>
        </div>
      )}
      
      {/* Footer Banner */}
      <div className="mt-16 text-center">
        <p className="text-gray-500">
          Powered by <span className="font-medium">NutriGen Bot</span> - Your Personal Nutrition Assistant
        </p>
      </div>
    </div>
  );
};

export default HomePage;
